mapper.select:
select NVL(count(*), 0) from plan where plan_num in ( select plan_num from ( select p.plan_num plan_num, p.mem_id mem_id, p.plan_title plan_title, p.plan_startDate plan_startDate, p.plan_stays plan_stays, p.plan_img plan_img, p.plan_public plan_public, d.planDetail_country country, d.planDetail_city city from plan p, planDetail d where p.plan_num = d.plan_num and p.plan_public = 0 )
where ${field} like '%'||#{keyword}||'%'
)

select * from ( select aa.*, rownum rnum from ( select * from plan where plan_num in ( select plan_num from ( select p.plan_num plan_num, p.mem_id mem_id, p.plan_title plan_title, p.plan_startDate plan_startDate, p.plan_stays plan_stays, p.plan_img plan_img, p.plan_public plan_public, d.planDetail_country country, d.planDetail_city city from plan p, planDetail d where p.plan_num = d.plan_num and p.plan_public = 0 )
where ${field} like '%'||#{keyword}||'%'
) order by plan_num desc )aa ) where rnum >= #{startRow} and rnum <= #{endRow}
