mapper.sql:
select ro.* from room ro where ro.room_num in ( select NVL(y.room_num, -1) room_num from ( select r.room_num from room r
, ( select NVL(z.room_num, -1) room_num from ( select rs.room_num room_num from rsvn rs, rsvnPay rp where rs.rsvn_num = rp.rsvn_num and rp.rsvnPay_stat = 0 and rs.rsvn_checkin between to_date(#{checkin}, 'yyyy-mm-dd') and (to_date(#{checkout}, 'yyyy-mm-dd') - 1) or rs.rsvn_checkout - 1 between to_date(#{checkin}, 'yyyy-mm-dd') and (to_date(#{checkout}, 'yyyy-mm-dd') - 1) )z right outer join dual on 1 = 1 )aa
where r.roomChk = 0
and r.room_capa >= #{cnt}
and r.room_num not in aa.room_num
)y right outer join dual on 1 = 1 )

mapper.select:
select * from ( select acc.accom_num accom_num, acc.sell_num sell_num, acc.accom_name accom_name, acc.accom_addr accom_addr, acc.accom_comm accom_comm, acc.accom_country accom_country, acc.accom_city accom_city, acc.accommImg_orgImg accommImg_orgImg, acc.accommImg_saveImg accommImg_saveImg, acc.accomchk accomchk, rr.room_price room_price, y.star star, w.cnt reviewCnt, rownum rnum from (select accom_num, NVL(min(room_price), 0) room_price from room where roomchk = 0 group by accom_num) rr, ( select yy.accom_num accom_num, NVL(yyy.star, 0) star from accom yy left outer join ( select acco.accom_num accom_num, NVL(ceil(avg(rrv.resReview_star)), 0) star from rsvn rsv, reservationReview rrv, room roo, accom acco where rsv.rsvn_num = rrv.rsvn_num and rsv.room_num = roo.room_num and roo.accom_num = acco.accom_num group by acco.accom_num )yyy on yy.accom_num = yyy.accom_num )y, ( select ww.accom_num accom_num, NVL(www.cnt, 0) cnt from accom ww left outer join ( select acco.accom_num accom_num, NVL(count(*),0) cnt from rsvn rsv, reservationReview rrv, room roo, accom acco where rsv.rsvn_num = rrv.rsvn_num and rsv.room_num = roo.room_num and roo.accom_num = acco.accom_num group by acco.accom_num )www on ww.accom_num = www.accom_num )w, ( select distinct a.accom_num accom_num from accom a, (
)x where a.accomchk = 0 and a.accom_num = x.accom_num
and a.accom_name like '%'||#{keyword}||'%' or a.accom_city like '%'||#{keyword}||'%'
order by a.accom_num desc )bb, accom acc where bb.accom_num = rr.accom_num and bb.accom_num = acc.accom_num and y.accom_num = bb.accom_num and w.accom_num = bb.accom_num ) where rnum >= #{startRow} and rnum <= #{endRow}
order by star desc, reviewCnt desc

